import React, { useEffect, useState } from 'react';
import {
  Card,
  CardContent,
  Typography,
  Snackbar,
  Alert,
  List,
  ListItem,
  ListItemText,
  Divider,
  Avatar,
} from '@mui/material';

// Dummy data for tariffs
const dummyTariffs = [
  { time: '08:00 AM', price: 0.12 },
  { time: '12:00 PM', price: 0.20 },
  { time: '06:00 PM', price: 0.30 },
  { time: '10:00 PM', price: 0.10 },
];

function Notifications() {
  const [highPriceTimes, setHighPriceTimes] = useState([]);
  const [openSnackbar, setOpenSnackbar] = useState(false);

  useEffect(() => {
    const checkTariffs = () => {
      const highPrices = dummyTariffs.filter(tariff => tariff.price > 0.25); // Assuming price > $0.25 is high
      if (highPrices.length > 0) {
        setHighPriceTimes(highPrices);
        setOpenSnackbar(true);
      }
    };

    checkTariffs();

    // Check every minute for updates
    const interval = setInterval(checkTariffs, 60000);

    return () => clearInterval(interval); // Cleanup on unmount
  }, []);

  const handleCloseSnackbar = () => {
    setOpenSnackbar(false);
  };

  return (
    <Card variant="outlined" style={{ marginBottom: '20px', borderRadius: '8px' }}>
      <CardContent>
        <Typography variant="h5" style={{ marginBottom: '10px', textAlign: 'center' }}>
          User Notifications
        </Typography>
        
        {highPriceTimes.length === 0 ? (
          <Typography variant="body1" style={{ textAlign: 'center' }}>
            No high tariff alerts at this time.
          </Typography>
        ) : (
          <List>
            {highPriceTimes.map((tariff) => (
              <div key={tariff.time}>
                <ListItem>
                  <Avatar 
                    alt="Solar Energy Logo"
                    src="/path/to/your/logo.png" // Replace with your logo path
                    style={{ marginRight: '10px' }}
                  />
                  <ListItemText 
                    primary={`High Price Alert!`} 
                    secondary={`Time: ${tariff.time} - Price: $${tariff.price}`} 
                  />
                </ListItem>
                <Divider />
              </div>
            ))}
          </List>
        )}
        
        <Snackbar open={openSnackbar} autoHideDuration={6000} onClose={handleCloseSnackbar}>
          <Alert onClose={handleCloseSnackbar} severity="warning" sx={{ width: '100%' }}>
            High electricity prices detected! Use solar energy!
          </Alert>
        </Snackbar>
      </CardContent>
    </Card>
  );
}

export default Notifications;
