import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement } from 'chart.js';
import { Card, CardContent, Typography, CircularProgress } from '@mui/material';
import axios from 'axios';

// Register the required components
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement);

function EnergyAnalytics() {
  const [loading, setLoading] = useState(true);
  const [energyData, setEnergyData] = useState([]);
  const [labels, setLabels] = useState([]);

  useEffect(() => {
    const fetchEnergyData = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/energy-data'); // Ensure this URL matches your server
        const data = response.data;

        setLabels(data.timestamps);
        setEnergyData(data.consumption);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching energy data:", error);
        setLoading(false);
      }
    };

    fetchEnergyData();
    
    const interval = setInterval(fetchEnergyData, 60000); // Fetch new data every minute

    return () => clearInterval(interval); // Cleanup on unmount
  }, []);

  const chartData = {
    labels: labels,
    datasets: [
      {
        label: 'Energy Consumption (kWh)',
        data: energyData,
        fill: false,
        backgroundColor: 'rgba(75,192,192,0.4)',
        borderColor: 'rgba(75,192,192,1)',
      },
    ],
  };

  return (
    <Card variant="outlined" style={{ marginBottom: '20px' }}>
      <CardContent>
        <Typography variant="h5" style={{fontWeight: 'bolder', fontFamily: 'serif', textAlign: 'center', textTransform: 'uppercase'}}>Energy Consumption Analytics</Typography>
        
        {loading ? (
          <CircularProgress />
        ) : (
          <>
            <Line data={chartData} />
            <Typography variant="body2" style={{ marginTop: '10px' }}>
              This chart shows your energy consumption over time.
            </Typography>
          </>
        )}
      </CardContent>
    </Card>
  );
}

export default EnergyAnalytics;
