import React, { useState } from 'react';
import {
  Card,
  CardContent,
  Typography,
  Button,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Snackbar,
  Alert,
  Grid,
} from '@mui/material';

const appliances = [
  { id: 1, name: 'Washing Machine', power: 2 }, // Power in kW
  { id: 2, name: 'Dishwasher', power: 1.5 },
  { id: 3, name: 'Electric Vehicle Charger', power: 7 },
];

const lowTariffTimes = ['08:00 AM', '10:00 PM']; // Example low tariff times

function SmartScheduler() {
  const [selectedAppliance, setSelectedAppliance] = useState('');
  const [scheduledTime, setScheduledTime] = useState('');
  const [openSnackbar, setOpenSnackbar] = useState(false);

  const handleSchedule = () => {
    if (selectedAppliance && scheduledTime) {
      setOpenSnackbar(true);
      // Here you would integrate with your backend to save the schedule
    } else {
      alert("Please select an appliance and a time.");
    }
  };

  const handleCloseSnackbar = () => {
    setOpenSnackbar(false);
  };

  return (
    <Card variant="outlined" style={{ marginBottom: '20px', borderRadius: '8px' }}>
      <CardContent>
        <Typography variant="h5" style={{ marginBottom: '20px', textAlign: 'center', fontFamily: 'serif', textTransform: 'uppercase' }}>
          Smart Scheduling
        </Typography>
        
        <Grid container spacing={2}>
          <Grid item xs={12} md={6}>
            <FormControl fullWidth>
              <InputLabel id="appliance-select-label">Select Appliance</InputLabel>
              <Select
                labelId="appliance-select-label"
                value={selectedAppliance}
                onChange={(e) => setSelectedAppliance(e.target.value)}
                variant="outlined"
              >
                {appliances.map((appliance) => (
                  <MenuItem key={appliance.id} value={appliance.id}>
                    {appliance.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>

          <Grid item xs={12} md={6}>
            <FormControl fullWidth>
              <InputLabel id="time-select-label">Select Time</InputLabel>
              <Select
                labelId="time-select-label"
                value={scheduledTime}
                onChange={(e) => setScheduledTime(e.target.value)}
                variant="outlined"
              >
                {lowTariffTimes.map((time) => (
                  <MenuItem key={time} value={time}>
                    {time}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>

          <Grid item xs={12}>
            <Button 
              variant="contained" 
              color="primary" 
              onClick={handleSchedule}
              fullWidth
              style={{ padding: '10px' }}
            >
              Schedule Appliance
            </Button>
          </Grid>
        </Grid>

        <Snackbar open={openSnackbar} autoHideDuration={6000} onClose={handleCloseSnackbar}>
          <Alert onClose={handleCloseSnackbar} severity="success" sx={{ width: '100%' }}>
            Scheduled successfully!
          </Alert>
        </Snackbar>
      </CardContent>
    </Card>
  );
}

export default SmartScheduler;
