// src/components/TariffMonitor.js
import React, { useEffect } from 'react';
import axios from 'axios';
import { Card, CardContent, Typography, CircularProgress } from '@mui/material';
import { useEnergy } from '../context/EnergyContext';

function TariffMonitor() {
  const { state, dispatch } = useEnergy();
  
  useEffect(() => {
    const fetchTariffs = async () => {
      const response = await axios.get('http://localhost:5000/api/tariffs');
      dispatch({ type: 'SET_TARIFFS', payload: response.data });
    };

    fetchTariffs();
    
    const interval = setInterval(fetchTariffs, 30000); // Fetch every 30 seconds

    return () => clearInterval(interval); // Cleanup on unmount
  }, [dispatch]);

  return (
    <Card variant="outlined" style={{ marginBottom: '20px' }}>
      <CardContent>
        <Typography variant="h5">Real-Time Tariff Monitoring</Typography>
        {state.tariffs.length === 0 ? (
          <CircularProgress />
        ) : (
          state.tariffs.map((tariff) => (
            <Typography key={tariff.id}>
              Time: {tariff.time} - Price: ${tariff.price}
            </Typography>
          ))
        )}
      </CardContent>
    </Card>
  );
}

export default TariffMonitor;
