import React, { useEffect, useState } from 'react';
import { Card, CardContent, Typography, CircularProgress } from '@mui/material';
import axios from 'axios';

function SolarManagement() {
  const [loading, setLoading] = useState(true);
  const [solarData, setSolarData] = useState({
    production: 0,
    consumption: 0,
    batteryLevel: 0,
  });

  // Dummy data for solar management
  const fetchDummyData = () => {
    // Simulating fetching data
    const dummyData = {
      production: Math.floor(Math.random() * 100) + 1, // Random production between 1 and 100 kWh
      consumption: Math.floor(Math.random() * 100) + 1, // Random consumption between 1 and 100 kWh
      batteryLevel: Math.floor(Math.random() * 100) + 1, // Random battery level between 1 and 100%
    };
    return dummyData;
  };

  useEffect(() => {
    const fetchData = () => {
      const data = fetchDummyData();
      setSolarData(data);
      setLoading(false);
    };

    fetchData();

    // Fetch new data every minute
    const interval = setInterval(fetchData, 60000);

    return () => clearInterval(interval); // Cleanup on unmount
  }, []);

  return (
    <Card variant="outlined" style={{ marginBottom: '20px' }}>
      <CardContent>
        <Typography variant="h5">Solar Energy Management</Typography>
        
        {loading ? (
          <CircularProgress />
        ) : (
          <>
            <Typography variant="body1">
              <strong>Solar Production:</strong> {solarData.production} kWh
            </Typography>
            <Typography variant="body1">
              <strong>Energy Consumption:</strong> {solarData.consumption} kWh
            </Typography>
            <Typography variant="body1">
              <strong>Battery Level:</strong> {solarData.batteryLevel}%
            </Typography>
          </>
        )}
      </CardContent>
    </Card>
  );
}

export default SolarManagement;
